	// Función para convertir un polygon CSS a SVG path
	function singlePolygonToPath(polygon: string, x: number, y: number, width: number, height: number): string {
		const coords = polygon
			.replace('polygon(', '')
			.replace(')', '')
			.split(',')
			.map(p => p.trim());

		return coords
			.map((coord, i) => {
				const parts = coord.split(/\s+/);
				const px = parseFloat(parts[0].replace('%', ''));
				const py = parseFloat(parts[1].replace('%', ''));
				// Convertir porcentajes relativos a coordenadas absolutas
				const absX = x + (px / 100) * width;
				const absY = y + (py / 100) * height;
				return i === 0 ? `M${absX} ${absY}` : `L${absX} ${absY}`;
			})
			.join(' ') + ' Z';
	}

	// Función que convierte uno o múltiples polygons a un array de paths SVG
	// Soporta: string, string[], o Forma[] (cada forma con su propia posición/tamaño)
	function polygonsToPaths(clipPath: ClipPathType, x: number, y: number, width: number, height: number): string[] {
		// Si es un string simple
		if (typeof clipPath === 'string') {
			return [singlePolygonToPath(clipPath, x, y, width, height)];
		}

		// Si es un array
		if (Array.isArray(clipPath)) {
			// Verificar si es array de Forma (objetos) o array de strings
			if (clipPath.length > 0 && typeof clipPath[0] === 'object') {
				// Es un array de Forma - cada una con su propia posición/tamaño
				return (clipPath as Forma[]).map(forma =>
					singlePolygonToPath(forma.polygon, forma.x, forma.y, forma.width, forma.height)
				);
			} else {
				// Es un array de strings - usar posición/tamaño del municipio
				return (clipPath as string[]).map(polygon =>
					singlePolygonToPath(polygon, x, y, width, height)
				);
			}
		}

		return [];
	}

